#!/usr/local/bin/python3# -*- coding: utf-8 -*-from tkinter import *#Создание нового класса похожего на Frameclass App_frame(Frame):	def __init__(self, parent):		Frame.__init__(self, parent, background = "white")		#Инициализация главного Frame 		self.parent = parent									#Присвоение методу parent главного фрейма переданного значения parent		self.parent.title("Hello")								#Установка имени главного окна		self.pack(fill = BOTH, expand = 1)						#Отображение и заполнение главного окна фреймом		self.center_window()									#Вызов функции центрирования	def center_window(self):		width = 250												#Установка ширины окна		height = 250											#Установка высоты окна		width_screen = self.parent.winfo_screenwidth()			#Запрос ширины экрана		height_screen = self.parent.winfo_screenheight()		#Запрос высоты экрана		padding_width = (width_screen - width)/2				#Расчёт отступа слева		padding_height = (height_screen - height)/2				#Расчёт отступа сверху		self.parent.geometry('%dx%d+%d+%d' \			% (width, height, padding_width, padding_height))	#Установка положения и размеров окна#Функция создания главного окнаdef main():	main_window = Tk()											#Создание окна	main_frame = App_frame(main_window)							#Создание главного фрейма внутри главного окна	main_window.mainloop()										#Основной цикл#Первичная инициализация программыif __name__ == '__main__':	main()