#!/usr/local/bin/python3# -*- coding: utf-8 -*-from tkinter import *class Start(Frame):	def __init__(self, parent):		Frame.__init__(self, parent, background = "green")		self.parent = parent		self.initUI()	def initUI(self):		self.parent.title("Hello")		self.pack(fill = BOTH, expand = 1)def main():	main_window = Tk()	main_window.geometry("250x250+100+100")	top_frame = Start(main_window)	main_window.mainloop()if __name__ == '__main__':	main()